mapper.select:

select SLCTN_NOTI_NO slctnNotiNo, ADMIN_NO adminNo, ORGNZ_NO orgnzNo, SLCTN_NOTI_DSTNCT slctnNotiDstnct, SLCTN_TITLE slctnTitle
from slctn_noti
where SLCTN_NOTI_DSTNCT like concat('%',#{type},'%');

mapper.update:
update slctn_noti set ADMIN_NO=#{notiVo.adminNo }, ORGNZ_NO=#{notiVo.orgnzNo }, NOTI_REGST_DT=now(), SLCTN_BEGIN_DT=#{notiVo.slctnBeginDt }, SLCTN_END_DT=#{notiVo.slctnEndDt }, SLCTN_TITLE=#{notiVo.slctnTitle }, SLCTN_TEXT=#{notiVo.slctnText }, SLCTN_NOPS=#{notiVo.slctnNops }, SLSN_MTH=#{notiVo.slsnMth }, SBMT_DOC_TEXT=#{notiVo.sbmtDocText }, CHRGR_DSTNCT='비회원', CHRGR_NM=#{notiVo.chrgrNm }, CHRGR_TEL_NO=#{notiVo.chrgrTelNo }, CHRGR_EMAIL=#{notiVo.chrgrEmail }, SUPRT_QUALF=#{notiVo.suprtQualf },
SLCTN_NOTI_DSTNCT='대학원', SLCTN_NOTI_URL=#{notiVo.slctnNotiUrl }, RCP_MTH=#{notiVo.rcpMth }, SLSN_PLAC=#{notiVo.slsnPlac }, SCCN_ANCM_DD=#{notiVo.sccnAncmDd }, SCCN_ANCM_MTH=#{notiVo.sccnAncmMth }, REGST_BEGIN_DD=#{notiVo.regstBeginDd }, REGST_END_DD=#{notiVo.regstEndDd }, REGST_MTH=#{notiVo.regstMth }, SLSN_BEGIN_DD=#{notiVo.slsnBeginDd }, SLSN_END_DD=#{notiVo.slsnEndDd }
SLCTN_NOTI_DSTNCT='연구실', TUITION_SUPRT_YN=ifnull(#{notiVo.tuitionSuprtYn },'N'),LVNC_SUPRT_YN=ifnull(#{notiVo.lvncSuprtYn },'N'),BK21_EORGNZ_SUPRT_YN=ifnull(#{notiVo.bk21EorgnzSuprtYn },'N'), INCET_SUPRT_YN=ifnull(#{notiVo.incetSuprtYn },'N'),LRSTY_JOINGFEE_SUPRT_YN=ifnull(#{notiVo.lrstyJoingfeeSuprtYn },'N'),DORM_SUPRT_YN=ifnull(#{notiVo.dormSuprtYn },'N'), INTERN_YN=ifnull(#{notiVo.internYn },'N'),LRSTY_JOING_YN=ifnull(#{notiVo.lrstyJoingYn },'N'),RESRCH_TASK_JOING_YN=ifnull(#{notiVo.resrchTaskJoingYn },'N'), STDTR_OPPTY_SUPRT_YN=ifnull(#{notiVo.stdtrOpptySuprtYn },'N'),EDU_PGM_JOING_SUPRT_YN=ifnull(#{notiVo.eduPgmJoingSuprtYn },'N'),SUPRT_FVR_TEXT=#{notiVo.suprtFvrText }, ONLINE_SUPRT_YN=ifnull(#{notiVo.onlineSuprtYn },'N'),PRMRY_RESRCH_FIELD=#{notiVo.prmryResrchField }
where SLCTN_NOTI_NO=#{notiVo.slctnNotiNo }
