mapper.select:
	
select orgnz_no orgnzNo, orgnz_full_nm orgnzFullNm, ENG_ORGNZ_NM engOrgnzNm, ORGNZ_DSTNCT orgnzDstnct
from organz
where ORGNZ_DSTNCT like #{type}

	
select orgnz_no orgnzNo, orgnz_nm orgnzNm, ENG_ORGNZ_NM engOrgnzNm, ORGNZ_DSTNCT orgnzDstnct, HMPAGE_URL hmpageUrl, TEL_NO telNo, FAX_NO faxNo, ADDR addr, DETAIL_ADDR detailAddr, PRNTS_ORGNZ_NO prntsOrgnzNo, MAP_PROF_NM mapProfNm, LAB_INTRO labIntro, RSRCHR_INFO_TEXT rsrchrInfoText, 
HMNBRD_DSTNCT hmnbrdDstnct 
from organz
where ORGNZ_DSTNCT=#{type}
and orgnz_no = #{no};


select RESRCH_ACRSLT_NO resrchAcrsltNo,ORGNZ_NO orgnzNo,ACRSLT_DSTNCT acrsltDstnct,REGST_DT regstDt,RESRCH_YYCL resrchYycl,RESRCH_TEXT resrchText
from resrch_acrslt
where ORGNZ_NO=#{orgnzNo}


select RESRCH_ACRSLT_NO resrchAcrsltNo,ORGNZ_NO orgnzNo,ACRSLT_DSTNCT acrsltDstnct,REGST_DT regstDt,RESRCH_YYCL resrchYycl,RESRCH_TEXT resrchText
from resrch_acrslt
where RESRCH_ACRSLT_NO=#{resrchAcrsltNo}

mapper.insert:
		
		insert into organz (ORGNZ_NM, ENG_ORGNZ_NM, ORGNZ_DSTNCT, HMPAGE_URL, TEL_NO, FAX_NO, ADDR, DETAIL_ADDR, PRNTS_ORGNZ_NO, HMNBRD_DSTNCT, orgnz_full_nm)
		values(#{orgnzNm}, #{engOrgnzNm}, #{orgnzDstnct}, #{hmpageUrl}, #{telNo}, #{faxNo}, #{addr}, #{detailAddr}, 
		

		
		 
		 0, #{hmnbrdDstnct}, #{orgnzNm});
		
		
		
		 
		 #{prntsOrgnzNo}, #{hmnbrdDstnct},concat(#{orgnzFullNm},' ',#{orgnzNm}));
		
		
		
		
		 
        SELECT LAST_INSERT_ID()

mapper.update:
		
		update organz
		set HMNBRD_DSTNCT=#{hmnbrdDstnct}, ORGNZ_DSTNCT=#{orgnzDstnct}, ORGNZ_NM=#{orgnzNm}, ENG_ORGNZ_NM=#{engOrgnzNm}, HMPAGE_URL=#{hmpageUrl}, TEL_NO=#{telNo}, FAX_NO=#{faxNo}, 
			ADDR=#{addr}, DETAIL_ADDR=#{detailAddr}, 
		

		
					
			PRNTS_ORGNZ_NO=#{prntsOrgnzNo}
		
		

		
					
			PRNTS_ORGNZ_NO=null
		
		
		
		
		where ORGNZ_NO=#{orgnzNo};

mapper.select:

select ORGNZ_NO orgnzNo, ORGNZ_NM orgnzNm, ORGNZ_DSTNCT orgnzDstnct, orgnz_full_nm orgnzFullNm
from organz 
where ORGNZ_DSTNCT like #{type}
and left(ORGNZ_NM,1) between #{start} and #{end}
order by 2;

			
			SELECT  o2.orgnz_no orgnzNo, hierarchy_sys_connect_by_textpath(' ', o2.orgnz_no) AS orgnzNm, o2.ORGNZ_DSTNCT orgnzDstnct
			FROM    (SELECT  hierarchy_connect_by_parent_eq_prior_id_with_level(ORGNZ_NO, 4) AS id,
                CAST(@level AS SIGNED) AS level
        			FROM    (SELECT  @start_with := 0,
                        @id := @start_with,
                        @level := 0
                ) vars, organz
        	WHERE   @id IS NOT NULL) o1 JOIN    organz o2 ON      o2.ORGNZ_NO = o1.id
			where hierarchy_sys_connect_by_textpath('\n', o2.orgnz_no) like '%${stext }%'
			and o2.ORGNZ_DSTNCT = #{type }
